/**
 * EMERGENCY FORCE IDENTICAL DATA
 * LANGSUNG EKSEKUSI untuk memaksa data PERSIS SAMA
 */
class EmergencyDataForcer {
    
    private val firestore = FirebaseFirestore.getInstance()
    
    /**
     * LANGSUNG FORCE DATA IDENTIK - NO QUESTIONS ASKED!
     */
    suspend fun FORCE_IDENTICAL_DATA_NOW(): Boolean {
        return try {
            Log.d("EmergencyDataForcer", "üö® EMERGENCY FORCING IDENTICAL DATA NOW!")
            
            // 1. HAPUS SEMUA DATA EXISTING
            clearAllExistingData()
            
            // 2. ADD DATA IDENTIK YANG PERSIS SAMA
            val identicalFoods = getExactIdenticalFoods()
            
            identicalFoods.forEach { food ->
                val docRef = firestore.collection("foods").document()
                val foodWithId = food.copy(id = docRef.id)
                docRef.set(foodWithId).await()
                Log.d("EmergencyDataForcer", "‚úÖ FORCED IDENTICAL: ${food.name} - Rp ${food.price}")
            }
            
            Log.d("EmergencyDataForcer", "üéâ SUCCESSFULLY FORCED ${identicalFoods.size} IDENTICAL FOODS!")
            true
        } catch (e: Exception) {
            Log.e("EmergencyDataForcer", "‚ùå EMERGENCY FORCE FAILED", e)
            false
        }
    }
    
    private suspend fun clearAllExistingData() {
        try {
            val existing = firestore.collection("foods").get().await()
            existing.documents.forEach { doc ->
                doc.reference.delete().await()
            }
            Log.d("EmergencyDataForcer", "üóëÔ∏è EMERGENCY CLEARED ALL ${existing.size()} existing foods")
        } catch (e: Exception) {
            Log.e("EmergencyDataForcer", "‚ùå Failed to clear all data", e)
        }
    }
    
