import kotlinx.coroutines.tasks.await

/**
 * FORCE SAME DATA GENERATOR
 * Memaksa kedua app menggunakan data yang PERSIS SAMA
 */
class ForceSameDataGenerator {
    
    private val firestore = FirebaseFirestore.getInstance()
    
    /**
     * Generate EXACT SAME DATA untuk kedua app
     */
    suspend fun forceExactSameData(): Boolean {
        return try {
            Log.d("ForceSameDataGenerator", "üî• FORCING EXACT SAME DATA...")
            
            // Hapus SEMUA data existing
            clearAllExistingData()
            
            // Add EXACT SAME data yang akan digunakan kedua app
            val exactSameData = getExactSameDataForBothApps()
            
            exactSameData.forEach { food ->
                val docRef = firestore.collection("foods").document()
                val foodWithId = food.copy(id = docRef.id)
                docRef.set(foodWithId).await()
                Log.d("ForceSameDataGenerator", "‚úÖ FORCED SAME: ${food.name} - ${food.price}")
            }
            
            Log.d("ForceSameDataGenerator", "üéâ FORCED ${exactSameData.size} EXACT SAME FOODS!")
            true
        } catch (e: Exception) {
            Log.e("ForceSameDataGenerator", "‚ùå Failed to force same data", e)
            false
        }
    }
    
    private suspend fun clearAllExistingData() {
        try {
            val existing = firestore.collection("foods").get().await()
            existing.documents.forEach { doc ->
                doc.reference.delete().await()
            }
            Log.d("ForceSameDataGenerator", "üóëÔ∏è CLEARED ALL ${existing.size()} existing foods")
        } catch (e: Exception) {
            Log.e("ForceSameDataGenerator", "‚ùå Failed to clear all data", e)
        }
    }
    
